     "totalItems": 36,
     "selfLink": "https://www.googleapis.com/plus/v1/activities/z133hhliztnxfnbsd22ld1jolnn5cjx2m/people/plusoners"
    },
    "resharers": {
     "totalItems": 7,
     "selfLink": "https://www.googleapis.com/plus/v1/activities/z133hhliztnxfnbsd22ld1jolnn5cjx2m/people/resharers"
    }
